package com.operatore;

public class Relational_operatore {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a , b;
		a = 30;
		b = 20;
		
		System.out.println(" a == b =" +(a == b));      //false
		System.out.println("a != b =" +(a != b));       //true
		System.out.println("a > b =" +(a > b));        //true
		System.out.println("a < b =" +(a < b));        //false
		System.out.println("a >= b =" +(a >= b));      //true
		System.out.println("a <= b =" +(a <=b));       //false
		
	}

}
